mapper.select:
select *
from pet
where
customer_Index = #{customer_Index}
and
pet_Name = #{pet_Name}

mapper.update:
update Reservation 
set	reservation_Check = 'canceled' 
where reservation_Index = #{reservation_Index}

update Reservation
set review_Check = '1'
where reservation_Index = #{reservation_Index}

mapper.select:
select *
from Pet, Reservation, Company 
where Pet.pet_Index = Reservation.pet_Index 
	and Company.company_Index = Reservation.Company_Index 
	and Pet.customer_Index = #{customer_Index} 
	and Pet.pet_Name = #{pet_Name}
